var scopes = 'offline_access;https://api.gmelius.com/public/auth/boards/read;https://api.gmelius.com/public/auth/conversations/metadata;https://api.gmelius.com/public/auth/conversations/insert;https://api.gmelius.com/public/auth/boards/read;https://api.gmelius.com/public/auth/boards/modify';
var redirectUri = config.redirectUri;
var codeChallenge = 'D7sFU_KQ_StNgw3-smiBD-hhpzlN2LZVBA82p0Zeb5c';
var codeVerifier = 'thisNeedsToBeFixed';

var url = 'https://gmelius.io/oauth/authorize?client_id=' + config.clientId + '&code_challenge=' + codeChallenge + '&scope=' + scopes + '&redirect_uri=' + redirectUri;
var win = window.open(url, 'Authorization page', 'toolbar=no,scrollbars=no,location=no,statusbar=no,menubar=no,resizable=0,width=500,height=600,left=' + ((screen.width / 2) - 250) + ',top=' + ((screen.height / 2) - 250) + ',');

var self = this;

var intervalFn = function () {
	try {
		if (!win || !win.document) {
			window.clearInterval(pollTimer);
		} else {
			if (win.document.URL.indexOf('/callback') != -1) {
				win.innerWidth = 100;
				win.innerHeight = 100;
				win.screenX = screen.width;
				win.screenY = screen.height;
				window.clearInterval(pollTimer);
				url = win.document.URL;
				var pos = url.indexOf('code=');
				var token
				if(pos > -1) {
					token = url.substring(pos + 5);
					config.authorizationCode = token;
					config.codeVerifier = codeVerifier;
					config.codeChallenge = codeChallenge;
				}
				console.log('token: '+ token)
			}
			try { win.close(); } catch (e) { }
		}
	} catch (e) { }
};

var pollTimer = window.setInterval(function () { intervalFn.apply(self) }, 500);
